var five = require("../lib/johnny-five");
var board = new five.Board();

board.on("ready", function() {

  var image = [
    "01100110",
    "10011001",
    "10000001",
    "10000001",
    "01000010",
    "00100100",
    "00011000",
    "00000000"
  ];

  var matrix = new five.Led.Matrix({
    pins: {
      data: 2,
      cs: 3,
      clock: 4,
    },
    devices: 4
  });

  matrix.on();

  function heart() {
    matrix.draw(image);
  }

  // heart();

  matrix.print("Rick");


  this.repl.inject({
    matrix: matrix,
    heart: heart
  });
});
